INSERT INTO marketfy.PRODUCTS(total_in_inventory,title,price,description,image) VALUES (2,'Classic Red Pullover Hoodie',10,'Elevate your casual wardrobe with our Classic Red Pullover Hoodie. Crafted with a soft cotton blend for ultimate comfort, this vibrant red hoodie features a kangaroo pocket, adjustable drawstring hood, and ribbed cuffs for a snug fit. The timeless design ensures easy pairing with jeans or joggers for a relaxed yet stylish look, making it a versatile addition to your everyday attire.','https://i.imgur.com/1twoaDy.jpeg');
