INSERT INTO marketfy.PRODUCTS(total_in_inventory,title,price,description,image) VALUES (9,'Classic Navy Blue Baseball Cap',61,'Step out in style with this sleek navy blue baseball cap. Crafted from durable material, it features a smooth, structured design and an adjustable strap for the perfect fit. Protect your eyes from the sun and complement your casual looks with this versatile and timeless accessory.','https://i.imgur.com/R3iobJA.jpeg');
