CREATE TABLE marketfy.PRODUCTS(
	product_id INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    title VARCHAR (100) NOT NULL,
    price FLOAT NOT NULL,
    image LONGBLOB,
    description VARCHAR(500),
    total_in_inventory INT NOT NULL
);

