CREATE TABLE marketfy.USER_SELECTION(
	id INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    user_id INT NOT NULL,
    product_id INT NOT NULL,
    product_quantity INT NOT NULL,
    stored_in VARCHAR(8) NOT NULL
);

